package de.jonashackt.base64gular;

import static org.junit.Assert.assertNotNull;

import java.util.ArrayList;
import java.util.List;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.boot.test.WebIntegrationTest;
import org.springframework.http.MediaType;
import org.springframework.http.converter.ByteArrayHttpMessageConverter;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.web.client.RestTemplate;

import de.jonashackt.base64gular.pdfconverter.Pdf;

@RunWith(SpringJUnit4ClassRunner.class)
@SpringApplicationConfiguration(classes = Base64gularApplication.class)
@WebIntegrationTest("server.port:8080")
public class Base64gularApplicationTests {

    private RestTemplate restTemplate;

    @Before
    public void setUp() {
        ByteArrayHttpMessageConverter byteArrayHttpMessageConverter = new ByteArrayHttpMessageConverter();

        List<MediaType> supportedApplicationTypes = new ArrayList<MediaType>();
        MediaType pdfApplication = new MediaType("application","pdf");
        supportedApplicationTypes.add(pdfApplication);

        byteArrayHttpMessageConverter.setSupportedMediaTypes(supportedApplicationTypes);
        
        restTemplate = new RestTemplate();
        
        restTemplate.getMessageConverters().add(byteArrayHttpMessageConverter);
    }

    @Test
    public void restControllerConvertsPdf() throws Exception {
        // Given
        String url = "http://localhost:8080/converter";

        Pdf pdf = new Pdf();
        pdf.setBase64endcodedPdf("");

        //When
        byte[] response = restTemplate.postForObject(url, pdf, byte[].class);

        //Then
        assertNotNull(response);

    }
}
